import React, { useState } from 'react';
import { Button, Form, Input, Row, Col, message, Image } from 'antd';
import apiService from '../../api/api';

const AddCategory = ({ setModalChild, handleRefresh }) => {
    const [form] = Form.useForm();
    const [categoryImage, setCategoryImage] = useState(null);

    const onFinishFailed = (errorInfo) => {
        message.error('Vui lòng điền đầy đủ thông tin bắt buộc.');
    };

    const onFinish = async (values) => {
        const data = {
            categoryName: values.categoryName || '',
            imageUrl: values.imageUrl || '',
            description: values.description || '',
        };

        try {
            await apiService.addCategory(data);
            message.success('Danh mục được thêm thành công!');
            handleRefresh();
            setModalChild(null);
        } catch (e) {
            const errorMessage = e.response?.data?.message || e.message || 'Đã xảy ra lỗi khi thêm danh mục';
            message.error(errorMessage);
        }
    };

    const handleImageUrlChange = (e) => {
        setCategoryImage(e.target.value);
    };

    return (
        <div style={{ width: 650, margin: 'auto' }}>
            <Form
                form={form}
                name="themDanhMuc"
                layout="vertical"
                onFinish={onFinish}
                onFinishFailed={onFinishFailed}
                autoComplete="off"
            >
                <Row gutter={16}>
                    <Col xs={24} sm={16}>
                        <Form.Item
                            label="Tên Danh Mục"
                            name="categoryName"
                            rules={[
                                {
                                    required: true,
                                    message: 'Hãy nhập tên danh mục!',
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>

                        <Form.Item
                            label="Url Ảnh Hiển Thị"
                            name="imageUrl"
                        >
                            <Input onChange={handleImageUrlChange} placeholder="https://example.com/image.png" />
                        </Form.Item>

                        <Form.Item
                            label="Mô Tả Danh Mục"
                            name="description"
                            rules={[{ required: true, message: 'Hãy nhập mô tả danh mục!' }]}>
                            <Input.TextArea rows={4} />
                        </Form.Item>
                    </Col>

                    <Col xs={24} sm={8}>
                        <Form.Item label="Xem trước ảnh">
                            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', width: '140px', height: '140px', border: '1px dashed #d9d9d9', borderRadius: '8px', padding: '5px' }}>
                                {categoryImage ? (
                                    <Image
                                        style={{ maxHeight: '100%', maxWidth: '100%', objectFit: 'contain' }}
                                        src={categoryImage}
                                        fallback="data:image/png;base64,"
                                    />
                                ) : (
                                    <span style={{ color: '#bfbfbf' }}>Xem trước</span>
                                )}
                            </div>
                        </Form.Item>
                    </Col>
                </Row>

                <Form.Item style={{ textAlign: 'right', marginTop: '20px', marginBottom: 0 }}>
                    <Button type="default" onClick={() => setModalChild(null)} style={{ marginRight: 8 }}>
                        Hủy bỏ
                    </Button>
                    <Button type="primary" htmlType="submit">
                        Thêm Danh Mục
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default AddCategory;